const TagPath = require("fast-xml-parser/src/v5/TagPath");

class TagPathMatcher{
  constructor(stack,node){
    this.stack = stack;
    this.node= node;
  }

  match(path){
    const tagPath = new TagPath(path);
    return tagPath.match(this.stack, this.node);
  }
}

module.exports = TagPathMatcher;